import React from "react"
import { Container, Row, Col, Jumbotron } from "react-bootstrap"
import axios from "axios"

class UsersAndSensorsNumber extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            numberOfUser: "",
            numberOfSensor: "", 
            users: []
        }
    }
    //recuperation du nombre d'utilisateur 
    
    componentDidMount() {
        this.countUsers();
    }

    countUsers() {

        axios.get("http://localhost:3030/users/")
            .then(response => {
                this.setState({
                    users: response.data, 
                    nomberOfuser: this.state.users.length
                })
                console.log(this.state.users.length);
            })
    }

    render() {
        return (
            <div>
                <Jumbotron className="widget">
                <Container className="my-container">
                    <Row>
                        <Col xs="12" sm="12" md="12" lg="6">
                            <Row className="justify-content-center">
                                <h2>Users</h2>
                            </Row>
                            <Row className="justify-content-center">
                                <h3>{this.state.users.length}</h3>
                            </Row>
                        </Col>
                        <Col xs="12" sm="12" md="12" lg="6">
                            <Row className="justify-content-center">
                                <h2>Sensors</h2>
                            </Row>
                            <Row className="justify-content-center">
                                <h3>{this.state.numberOfSensor}</h3>
                            </Row>
                        </Col>
                    </Row>
                </Container>
                </Jumbotron>
                
            </div>
        )
    }
}

export default UsersAndSensorsNumber
