import React from "react"
import axios from "axios"
import { Table, Container, Row, Col } from "react-bootstrap"

//source : 
//https://www.youtube.com/watch?v=hzLDsxPGctY

//pour afficher les utilisateurs




class ShowUsers extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
    }


    componentDidMount() {
        this.afficherUsers();
    }

    afficherUsers() {

        axios.get("http://localhost:3030/users/")
            .then(response => {
                this.setState({
                    users: response.data
                })
                console.log(this.state.users);
            })
    }


    render() {
        return (

            <div>
                <Container className="my-container">
                    <Row className="justify-content-center">
                        <h1>Liste des Utilisateurs</h1>
                    </Row>
                    <Row className="my-row2">
                    <Table striped bordered hover variant="dark">
                        <tr>
                            <th>userId</th>
                            <th>Location</th>
                            <th>PersonsInHouse</th>
                            <th>HouseSize</th>
                        </tr>
                        {this.state.users.map(user => (
                            <li key={user._id}>
                                Id: {user._id} | location : {user.location} | PersonsInHouse : {user.personsInHouse}| HouseSize : {user.houseSize}
                            </li>
                        ))}
                    </Table>
                    </Row>
                </Container>
            </div>

        )
    }
}

export default ShowUsers
