import React from "react"
import axios from "axios"
import { Table, Container, Row, Col } from "react-bootstrap"

//source : 
//https://www.youtube.com/watch?v=hzLDsxPGctY

//pour afficher les utilisateurs




class ShowUsers extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
    }


    componentDidMount() {
        this.afficherUsers();
    }

    afficherUsers() {

        axios.get("http://localhost:3030/users/")
            .then(response => {
                this.setState({
                    users: response.data
                })
                console.log(this.state.users);
            })
    }


    render() {
        return (

            <div>
                <Container className="my-container">
                    <Row className="justify-content-center">
                        <h1>Liste des Utilisateurs</h1>
                    </Row>
                    <Row>
                        <Table striped bordered hover variant="dark">
                            <thead>
                                <tr>
                                    <Col className="my-col3"><th>userId</th></Col>
                                    <Col className="my-col3"><th>Location</th></Col>
                                    <Col className="my-col3"><th>PersonsInHouse</th></Col>
                                    <Col className="my-col3"><th>HouseSize</th></Col>
                                </tr>
                            </thead>
                            {this.state.users.map(user => (
                                <tbody key={user._id}>
                                    <tr>
                                        <td>{user._id}</td>
                                        <td>{user.location}</td>
                                        <td>{user.personsInHouse}</td>
                                        <td>{user.houseSize}</td>
                                    </tr>
                                </tbody>
                            ))}
                        </Table>
                    </Row>
                </Container>
            </div>

        )
    }
}

export default ShowUsers
